package pattern;

public class p6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1;i<=5;i++) {
			for(int j=1;j<=5;j++) {
				if(j==1||j==4) {
				System.out.print("* ");}
				else {System.out.print("1 ");}
			}System.out.println();
		}


	}

}
